import { useCallback, useEffect, useState, useRef } from "react"

function ListTodos({ todos, changeCompleteTodo, deleteTodo }) {
    const [filterTodos, setFilterTodos] = useState(todos)
    const todosRef = useRef()

    todosRef.current = todos

    useEffect(() => {
        setFilterTodos(todosRef.current)
    }, [todosRef.current])

    const handleFilterTodo = useCallback((filter) => {
        switch(filter) {
            case "completed":
                setFilterTodos(todosRef.current.filter(todo => todo.isComplete === true))
                break
            case "active":
                setFilterTodos(todosRef.current.filter(todo => todo.isComplete === false))
                break
            default: setFilterTodos(todosRef.current)
        }
    }, [])

    return (
        <>
            <ul>
                {filterTodos && filterTodos.map((todo) => (
                    <li key={todo.id}>
                        <input type="checkbox" checked={todo.isComplete} onChange={() => changeCompleteTodo(todo.id)}/>
                        <span className="todo-text">{todo.textTodo}</span>
                        <span className="deleteTodo" onClick={() => deleteTodo(todo.id)}>&times;</span>
                    </li>
                ))}
            </ul>
            <label>
                <input type="radio" name="filtered" onChange={() => handleFilterTodo("active")} />ACTIVE
            </label>
            <label>
                <input type="radio" name="filtered" onChange={() => handleFilterTodo("completed")} />COMPLETED
            </label>
            <label>
                <input type="radio" name="filtered" onChange={() => handleFilterTodo("all")} defaultChecked />ALL
            </label>
        </>
    )
}

export { ListTodos }
