import { useEffect, useState } from "react"
import { ListTodos } from "./ListTodos"

function FormTodos() {
    const [todos, setTodos] = useState([])
    const [textTodo, setTextTodo] = useState('')
    const [filterTodos, setFilterTodos] = useState(todos)

    const handleSubTodo = () => {
        if(textTodo.trim().length) {
            setTodos([
                ...todos,
                {
                    id: new Date().toISOString(),
                    textTodo,
                    isComplete: false
                }
            ])
            setTextTodo('')
        }
    }

    const changeCompleteTodo = (id) => {
        setTodos(filterTodos.map(todo =>
            todo.id !== id ? todo : { ...todo, isComplete: !todo.isComplete }))
    }

    const deleteTodo = (id) => {
        setTodos(filterTodos.filter(todo => todo.id !== id))
    }

    const handleFilterTodo = (filter) => {
        switch(filter) {
            case "active":
                setFilterTodos(todos)
                break
            case "completed":
                setFilterTodos([...todos].filter(todo => todo.isComplete === filter))
                break
            case "all":
                
                break
        }
    }

    return (
        <>
            <input type="text" placeholder="Type some todo..." onChange={(e) => setTextTodo(e.target.value)}/>
            <button onClick={handleSubTodo}>Enter</button>
            <ListTodos todos={filterTodos} changeCompleteTodo={changeCompleteTodo} deleteTodo={deleteTodo}/>
            <label>
                <input type="checkbox" onChange={() => handleFilterTodo("active")} />ACTIVE
            </label>
            <label>
                <input type="checkbox" onChange={() => handleFilterTodo("completed")} />COMPLETED
            </label>
            <label>
                <input type="checkbox" onChange={() => handleFilterTodo("all")} />ALL
            </label>
        </>
    )
}

export { FormTodos }
