import { useEffect, useState } from "react"

function ListTodos({ todos, setTodos, changeCompleteTodo, deleteTodo }) {
    const [filterTodos, setFilterTodos] = useState(todos)

    // const handleFilterTodo = (filter) => {
    //     switch(filter) {
    //         case "all":
    //             setFilterTodos(todos)
    //             break
    //         case "completed":
    //             setFilterTodos(todos.filter(todo => todo.isComplete === true))
    //             break
    //         case "active":
    //             setFilterTodos(todos.filter(todo => todo.isComplete === false))
    //             break
    //     }
    // }
    const handleFilterTodo = (filter) => {
        switch(filter) {
            case "all":
                setTodos(todos)
                break
            case "completed":
                setTodos(todos.filter(todo => todo.isComplete === true))
                break
            case "active":
                setTodos(todos.filter(todo => todo.isComplete === false))
                break
        }
    }

    return (
        <>
            <ul>
                {todos && todos.map((todo) => (
                    <li key={todo.id}>
                        <input type="checkbox" checked={todo.isComplete} onChange={() => changeCompleteTodo(todo.id)}/>
                        <span className="todo-text">{todo.textTodo}</span>
                        <span className="deleteTodo" onClick={() => deleteTodo(todo.id)}>&times;</span>
                    </li>
                ))}
            </ul>
            <label>
                <input type="radio" name="filtered" onChange={() => handleFilterTodo("active")} />ACTIVE
            </label>
            <label>
                <input type="radio" name="filtered" onChange={() => handleFilterTodo("completed")} />COMPLETED
            </label>
            <label>
                <input type="radio" name="filtered" onChange={() => handleFilterTodo("all")} />ALL
            </label>
        </>
    )
}

export { ListTodos }
