import { useState } from "react"

function ListTodos({ todos, changeCompleteTodo, deleteTodo }) {
    const [filterTodos, setFilterTodos] = useState([])

    const handleFilterTodo = (filter) => {
        switch(filter) {
            case "active":
                setFilterTodos(todos)
                break
            case "completed":
                setFilterTodos(todos.filter(todo => todo.isComplete === filter))
                break
        }
    }
    
    return (
        <>
            <ul>
                {todos && todos.map((todo) => (
                    <li key={todo.id}>
                        <input type="checkbox" checked={todo.isComplete} onChange={() => changeCompleteTodo(todo.id)}/>
                        <span className="todo-text">{todo.textTodo}</span>
                        <span className="deleteTodo" onClick={() => deleteTodo(todo.id)}>&times;</span>
                    </li>
                ))}
            </ul>
            <label>
                <input type="checkbox" onChange={() => handleFilterTodo("active")} />ACTIVE
            </label>
            <label>
                <input type="checkbox" onChange={() => handleFilterTodo("completed")} />COMPLETED
            </label>
            <label>
                <input type="checkbox" onChange={() => handleFilterTodo("all")} />ALL
            </label>
        </>
    )
}

export { ListTodos }
