import { useState } from "react"
import { ListTodos } from "./ListTodos"

function FormTodos() {
    const [todos, setTodos] = useState([])
    const [textTodo, setTextTodo] = useState('')

    const handleSubTodo = () => {
        setTodos([
            ...todos,
            {
                id: new Date().toISOString(),
                textTodo,
                isComplete: false
            }
        ])
    }

    return (
        <>
            <input type="text" placeholder="Type some todo..." onChange={(e) => setTextTodo(e.target.value)}/>
            <button onClick={handleSubTodo}>Enter</button>
            <ListTodos {...todos}/>
        </>
    )
}

export { FormTodos }
