import { useEffect, useState } from "react"
import { ListTodos } from "./ListTodos"

function FormTodos() {
    const [todos, setTodos] = useState([])
    const [textTodo, setTextTodo] = useState('')
    const [filterTodos, setFilterTodos] = useState('all')

    useEffect(() => {
        setFilterTodos(filterTodos)
    }, [])

    const handleSubTodo = () => {
        if(textTodo.trim().length) {
            setTodos([
                ...todos,
                {
                    id: new Date().toISOString(),
                    textTodo,
                    isComplete: false
                }
            ])
            setTextTodo('')
        }
    }

    const changeCompleteTodo = (id) => {
        setTodos(todos.map(todo =>
            todo.id !== id ? todo : { ...todo, isComplete: !todo.isComplete }))
    }

    const deleteTodo = (id) => {
        setTodos(todos.filter(todo => todo.id !== id))
    }

    const handleFilterTodo = (filter) => {
        switch(filter) {
            case "active":
                setFilterTodos(true)
                break
            default:
                setFilterTodos('all')
        }
        console.log(filterTodos)
        setTodos(todos.filter(todo => todo.isComplete === true))
    }

    return (
        <>
            <input type="text" placeholder="Type some todo..." onChange={(e) => setTextTodo(e.target.value)}/>
            <button onClick={handleSubTodo}>Enter</button>
            <ListTodos todos={todos} changeCompleteTodo={changeCompleteTodo} deleteTodo={deleteTodo}/>
            <input type="radio" onChange={() => handleFilterTodo("active")}/>
        </>
    )
}

export { FormTodos }
