import { useRef, useState } from "react"
import { ListTodos } from "./ListTodos"

function FormTodos() {
    const [todos, setTodos] = useState([])
    const [textTodo, setTextTodo] = useState('')
    const listRef = useRef([])

    const handleSubTodo = () => {
        if(textTodo.trim().length) {
            setTodos([
                ...todos,
                {
                    id: new Date().toISOString(),
                    textTodo,
                    isComplete: false
                }
            ])
        }
        setTextTodo('')
    }

    const changeCompleteTodo = (id) => {
        setTodos(todos.map(todo =>
            todo.id !== id ? todo : { ...todo, isComplete: !todo.isComplete }))
    }

    const deleteTodo = (id) => {
        setTodos(todos.filter(todo => todo.id !== id))
    }

    const editTodo = (n) => {
        let todoText = listRef.current[n].querySelector('.todo-text')
        if(!todoText.hasAttribute('contentEditable'))
            todoText.contentEditable = true
        else
            todoText.removeAttribute('contentEditable')
    }

    return (
        <>
            <input type="text" placeholder="Type some todo..." value={textTodo} onChange={(e) => setTextTodo(e.target.value)}/>
            <button onClick={handleSubTodo}>Enter</button>
            <ListTodos todos={todos} changeCompleteTodo={changeCompleteTodo}
            deleteTodo={deleteTodo} listRef={listRef} editTodo={editTodo}
            setTodos={setTodos}/>
        </>
    )
}

export { FormTodos }
