import { useEffect, useState } from "react"

function ListTodos ({ todos, changeCompleteTodo, deleteTodo }) {
    const [filterTodos, setFilterTodos] = useState(todos)
    
    useEffect(() => {
        setFilterTodos(todos)
    }, [todos])

    const handleFilterTodo = (filter) => {
        console.log("rerender")
        switch(filter) {
            case "completed":
                setFilterTodos(oldTodos => oldTodos.filter(todo => todo.isComplete === true))
                break
            case "active":
                setFilterTodos(oldTodos => oldTodos.filter(todo => todo.isComplete === false))
                break
            default: setFilterTodos(oldTodos => oldTodos)
        }
    }

    return (
        <>
            <ul>
                {filterTodos && filterTodos.map((todo) => (
                    <li key={todo.id}>
                        <input type="checkbox" checked={todo.isComplete} onChange={() => {
                            changeCompleteTodo(todo.id)
                            handleFilterTodo("active")
                        }}/>
                        <span className="todo-text">{todo.textTodo}</span>
                        <span className="deleteTodo" onClick={() => deleteTodo(todo.id)}>&times;</span>
                    </li>
                ))}
            </ul>
            <label>
                <input type="radio" name="filtered" onChange={() => handleFilterTodo("active")} />ACTIVE
            </label>
            <label>
                <input type="radio" name="filtered" onChange={() => handleFilterTodo("completed")} />COMPLETED
            </label>
            <label>
                <input type="radio" name="filtered" onChange={() => handleFilterTodo("all")} defaultChecked />ALL
            </label>
        </>
    )
}

export { ListTodos }
