import { useCallback, useEffect, useState } from "react"

function ListTodos ({ todos, changeCompleteTodo, deleteTodo, listRef, editTodo }) {
    const [filterTodos, setFilterTodos] = useState(todos)
    const [textFilter, setTextFilter] = useState('all')
    
    useEffect(() => {
        setFilterTodos(todos)
        setTextFilter(textFilter)
        handleFilterTodo(textFilter)
    }, [todos, textFilter])

    const handleFilterTodo = useCallback((filter) => {
        switch(filter) {
            case "completed":
                setFilterTodos(todos.filter(todo => todo.isComplete === true))
                break
            case "active":
                setFilterTodos(todos.filter(todo => todo.isComplete === false))
                break
            default: 
                setFilterTodos(todos)
        }
    }, [todos])

    return (
        <>
            <ul>
                {filterTodos && filterTodos.map((todo, i) => (
                    <li key={todo.id} ref={el => listRef.current[i] = el}>
                        <input type="checkbox" checked={todo.isComplete} onChange={() => changeCompleteTodo(todo.id)}/>
                        <span className="todo-text">{todo.textTodo}</span>
                        <span onClick={() => editTodo(i)}>&nbsp;Edit</span>
                        <span className="deleteTodo" onClick={() => deleteTodo(todo.id)}>&nbsp;&times;</span>
                    </li>
                ))}
            </ul>
            <label>
                <input type="radio" name="filtered" onChange={() => handleFilterTodo(setTextFilter("active"))} />ACTIVE
            </label>
            <label>
                <input type="radio" name="filtered" onChange={() => handleFilterTodo(setTextFilter("completed"))} />COMPLETED
            </label>
            <label>
                <input type="radio" name="filtered" onChange={() => handleFilterTodo(setTextFilter("all"))} defaultChecked />ALL
            </label>
        </>
    )
}

export { ListTodos }
