import { useCallback, useEffect, useMemo, useState } from "react"
import { ListTodos } from "./ListTodos"

function FormTodos() {
    const [todos, setTodos] = useState([])
    const [textTodo, setTextTodo] = useState('')

    const an = useMemo((e) => {
        setTextTodo(e.target.value)
    }, [])

    const handleSubTodo = () => {
        if(textTodo.trim().length) {
            setTodos([
                ...todos,
                {
                    id: new Date().toISOString(),
                    textTodo,
                    isComplete: false
                }
            ])
        }
        setTextTodo('')
    }

    const changeCompleteTodo = (id) => {
        setTodos(todos.map(todo =>
            todo.id !== id ? todo : { ...todo, isComplete: !todo.isComplete }))
    }

    const deleteTodo = (id) => {
        setTodos(todos.filter(todo => todo.id !== id))
    }

    return (
        <>
            <input type="text" placeholder="Type some todo..." value={textTodo} onChange={(e) => an(e)}/>
            <button onClick={handleSubTodo}>Enter</button>
            <ListTodos todos={todos} changeCompleteTodo={changeCompleteTodo} deleteTodo={deleteTodo}/>
        </>
    )
}

export { FormTodos }
