import { useEffect, useState, useRef } from "react"

function ListTodos({ todos, changeCompleteTodo, deleteTodo }) {
    const [filterTodos, setFilterTodos] = useState(todos)
    const filterTodoButton = useRef(null)

    useEffect(() => {
        setFilterTodos(todos)
    }, [todos])

    const handleFilterTodo = (filter) => {
        switch(filter) {
            case "completed":
                setFilterTodos(todos.filter(todo => todo.isComplete === true))
                break
            case "active":
                setFilterTodos(todos.filter(todo => todo.isComplete === false))
                break
            default: setFilterTodos(todos)
        }
    }

    return (
        <>
            <ul>
                {(() => {
                    if(filterTodoButton.current) {
                        filterTodos && filterTodos.map((todo) => (
                        <li key={todo.id}>
                            <input type="checkbox" checked={todo.isComplete} onChange={() => changeCompleteTodo(todo.id)}/>
                            <span className="todo-text">{todo.textTodo}</span>
                            <span className="deleteTodo" onClick={() => deleteTodo(todo.id)}>&times;</span>
                        </li>
                    ))
                    }
                })()}
            </ul>
            <label>
                <input type="radio" name="filtered" ref={filterTodoButton} onChange={() => handleFilterTodo("active")} />ACTIVE
            </label>
            <label>
                <input type="radio" name="filtered" ref={filterTodoButton} onChange={() => handleFilterTodo("completed")} />COMPLETED
            </label>
            <label>
                <input type="radio" name="filtered" ref={filterTodoButton} onChange={() => handleFilterTodo("all")} defaultChecked />ALL
            </label>
        </>
    )
}

export { ListTodos }
