import { useEffect, useRef, useState } from "react"

function ListTodos ({ todos, changeCompleteTodo, deleteTodo }) {
    const [filterTodos, setFilterTodos] = useState(todos)
    const inputRef = useRef([])
    
    useEffect(() => {
        setFilterTodos(todos)
    }, [todos])

    const handleFilterTodo = (filter) => {
        switch(filter) {
            case "completed":
                setFilterTodos(todos.filter(todo => todo.isComplete === true))
                break
            case "active":
                setFilterTodos(todos.filter(todo => todo.isComplete === false))
                break
            default: setFilterTodos(todos)
        }
    }

    return (
        <>
            <ul>
                {filterTodos && filterTodos.map((todo) => (
                    <li key={todo.id}>
                        <input type="checkbox" checked={todo.isComplete} onChange={() => changeCompleteTodo(todo.id)}/>
                        <span className="todo-text">{todo.textTodo}</span>
                        <span className="deleteTodo" onClick={() => deleteTodo(todo.id)}>&times;</span>
                    </li>
                ))}
            </ul>
            <label>
                <input type="radio" name="filtered" ref={el => inputRef.current[0] = el} onChange={() => handleFilterTodo("active")} />ACTIVE
            </label>
            <label>
                <input type="radio" name="filtered" ref={el => inputRef.current[1] = el} onChange={() => handleFilterTodo("completed")} />COMPLETED
            </label>
            <label>
                <input type="radio" name="filtered" ref={el => inputRef.current[2] = el} onChange={() => handleFilterTodo("all")} defaultChecked />ALL
            </label>
        </>
    )
}

export { ListTodos }
